import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterLink } from '@angular/router';
import { FormsModule } from '@angular/forms';
import { StudentService } from '../../services/student.service';
import { Student } from '../../models/student.model';

@Component({
  selector: 'app-student-list',
  standalone: true,
  imports: [CommonModule, RouterLink, FormsModule],
  template: `
    <div class="row">
      <div class="col-12">
        <div class="d-flex justify-content-between align-items-center mb-4">
          <h2 class="text-primary">
            <i class="fas fa-users me-2"></i>
            Students List
          </h2>
          <a routerLink="/add-student" class="btn btn-success">
            <i class="fas fa-plus me-2"></i>
            Add New Student
          </a>
        </div>

        <!-- Search Bar -->
        <div class="row mb-4">
          <div class="col-md-6">
            <div class="input-group">
              <span class="input-group-text">
                <i class="fas fa-search"></i>
              </span>
              <input 
                type="text" 
                class="form-control" 
                placeholder="Search students..." 
                [(ngModel)]="searchTerm"
                (input)="onSearch()">
            </div>
          </div>
          <div class="col-md-6">
            <select class="form-select" [(ngModel)]="selectedCourse" (change)="onCourseFilter()">
              <option value="">All Courses</option>
              <option value="Computer Science">Computer Science</option>
              <option value="Information Technology">Information Technology</option>
              <option value="Electronics">Electronics</option>
              <option value="Mechanical">Mechanical</option>
            </select>
          </div>
        </div>

        <!-- Students Table -->
        <div class="card shadow-sm">
          <div class="card-body">
            <div class="table-responsive">
              <table class="table table-hover">
                <thead class="table-primary">
                  <tr>
                    <th>ID</th>
                    <th>Name</th>
                    <th>Email</th>
                    <th>Roll Number</th>
                    <th>Course</th>
                    <th>Semester</th>
                    <th>Phone</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  <tr *ngFor="let student of students; let i = index" 
                      class="animate-row" 
                      [style.animation-delay]="i * 0.1 + 's'">
                    <td>{{ student.id }}</td>
                    <td>
                      <div class="d-flex align-items-center">
                        <div class="avatar me-2">
                          {{ student.name.charAt(0).toUpperCase() }}
                        </div>
                        {{ student.name }}
                      </div>
                    </td>
                    <td>{{ student.email }}</td>
                    <td>
                      <span class="badge bg-secondary">{{ student.rollNumber || 'N/A' }}</span>
                    </td>
                    <td>{{ student.course || 'N/A' }}</td>
                    <td>
                      <span class="badge bg-info" *ngIf="student.semester">
                        Sem {{ student.semester }}
                      </span>
                      <span class="text-muted" *ngIf="!student.semester">N/A</span>
                    </td>
                    <td>{{ student.phoneNumber || 'N/A' }}</td>
                    <td>
                      <div class="btn-group" role="group">
                        <a [routerLink]="['/edit-student', student.id]" 
                           class="btn btn-sm btn-outline-primary">
                          <i class="fas fa-edit"></i>
                        </a>
                        <button class="btn btn-sm btn-outline-danger" 
                                (click)="deleteStudent(student.id!)">
                          <i class="fas fa-trash"></i>
                        </button>
                      </div>
                    </td>
                  </tr>
                  <tr *ngIf="students.length === 0">
                    <td colspan="8" class="text-center text-muted py-4">
                      <i class="fas fa-users fa-3x mb-3 d-block"></i>
                      No students found
                    </td>
                  </tr>
                </tbody>
              </table>
            </div>
          </div>
        </div>

        <!-- Statistics Cards -->
        <div class="row mt-4">
          <div class="col-md-3">
            <div class="card bg-primary text-white">
              <div class="card-body">
                <div class="d-flex justify-content-between">
                  <div>
                    <h4>{{ students.length }}</h4>
                    <p class="mb-0">Total Students</p>
                  </div>
                  <i class="fas fa-users fa-2x opacity-75"></i>
                </div>
              </div>
            </div>
          </div>
          <div class="col-md-3">
            <div class="card bg-success text-white">
              <div class="card-body">
                <div class="d-flex justify-content-between">
                  <div>
                    <h4>{{ getUniqueCoursesCount() }}</h4>
                    <p class="mb-0">Courses</p>
                  </div>
                  <i class="fas fa-book fa-2x opacity-75"></i>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  `,
  styles: [`
    .avatar {
      width: 35px;
      height: 35px;
      border-radius: 50%;
      background: linear-gradient(45deg, #007bff, #0056b3);
      display: flex;
      align-items: center;
      justify-content: center;
      color: white;
      font-weight: 600;
      font-size: 0.9rem;
    }

    .animate-row {
      animation: slideInUp 0.6s ease-out forwards;
      opacity: 0;
      transform: translateY(20px);
    }

    @keyframes slideInUp {
      to {
        opacity: 1;
        transform: translateY(0);
      }
    }

    .table-hover tbody tr:hover {
      background-color: rgba(0, 123, 255, 0.05);
      transform: translateY(-1px);
      transition: all 0.2s ease;
    }

    .btn-group .btn {
      margin: 0 2px;
    }

    .card {
      border: none;
      transition: all 0.3s ease;
    }

    .card:hover {
      transform: translateY(-2px);
      box-shadow: 0 4px 15px rgba(0, 0, 0, 0.1) !important;
    }
  `]
})
export class StudentListComponent implements OnInit {
  students: Student[] = [];
  allStudents: Student[] = [];
  searchTerm: string = '';
  selectedCourse: string = '';

  constructor(private studentService: StudentService) { }

  ngOnInit(): void {
    this.loadStudents();
  }

  loadStudents(): void {
    this.studentService.getAllStudents().subscribe({
      next: (data) => {
        this.students = data;
        this.allStudents = data;
      },
      error: (error) => {
        console.error('Error loading students:', error);
      }
    });
  }

  onSearch(): void {
    if (this.searchTerm.trim() === '') {
      this.students = this.allStudents;
    } else {
      this.studentService.searchStudents(this.searchTerm).subscribe({
        next: (data) => {
          this.students = data;
        },
        error: (error) => {
          console.error('Error searching students:', error);
        }
      });
    }
  }

  onCourseFilter(): void {
    if (this.selectedCourse === '') {
      this.students = this.allStudents;
    } else {
      this.studentService.getStudentsByCourse(this.selectedCourse).subscribe({
        next: (data) => {
          this.students = data;
        },
        error: (error) => {
          console.error('Error filtering students:', error);
        }
      });
    }
  }

  deleteStudent(id: number): void {
    if (confirm('Are you sure you want to delete this student?')) {
      this.studentService.deleteStudent(id).subscribe({
        next: () => {
          this.loadStudents();
        },
        error: (error) => {
          console.error('Error deleting student:', error);
        }
      });
    }
  }

  getUniqueCoursesCount(): number {
    const courses = new Set(this.students.map(s => s.course).filter(c => c));
    return courses.size;
  }
}
