import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { Router, ActivatedRoute } from '@angular/router';
import { StudentService } from '../../services/student.service';
import { Student } from '../../models/student.model';

@Component({
  selector: 'app-student-form',
  standalone: true,
  imports: [CommonModule, FormsModule],
  template: `
    <div class="row justify-content-center">
      <div class="col-md-8">
        <div class="card shadow-sm">
          <div class="card-header bg-primary text-white">
            <h3 class="mb-0">
              <i class="fas fa-user-plus me-2"></i>
              {{ isEditMode ? 'Edit Student' : 'Add New Student' }}
            </h3>
          </div>
          <div class="card-body">
            <form (ngSubmit)="onSubmit()" #studentForm="ngForm">
              <div class="row">
                <div class="col-md-6 mb-3">
                  <label for="name" class="form-label">
                    <i class="fas fa-user me-1"></i>
                    Full Name *
                  </label>
                  <input 
                    type="text" 
                    class="form-control" 
                    id="name"
                    name="name"
                    [(ngModel)]="student.name" 
                    required
                    #name="ngModel"
                    [class.is-invalid]="name.invalid && name.touched">
                  <div class="invalid-feedback" *ngIf="name.invalid && name.touched">
                    Name is required
                  </div>
                </div>

                <div class="col-md-6 mb-3">
                  <label for="email" class="form-label">
                    <i class="fas fa-envelope me-1"></i>
                    Email Address *
                  </label>
                  <input 
                    type="email" 
                    class="form-control" 
                    id="email"
                    name="email"
                    [(ngModel)]="student.email" 
                    required
                    email
                    #email="ngModel"
                    [class.is-invalid]="email.invalid && email.touched">
                  <div class="invalid-feedback" *ngIf="email.invalid && email.touched">
                    <span *ngIf="email.errors?.['required']">Email is required</span>
                    <span *ngIf="email.errors?.['email']">Please enter a valid email</span>
                  </div>
                </div>
              </div>

              <div class="row" *ngIf="!isEditMode">
                <div class="col-md-6 mb-3">
                  <label for="password" class="form-label">
                    <i class="fas fa-lock me-1"></i>
                    Password *
                  </label>
                  <input 
                    type="password" 
                    class="form-control" 
                    id="password"
                    name="password"
                    [(ngModel)]="student.password" 
                    required
                    minlength="6"
                    #password="ngModel"
                    [class.is-invalid]="password.invalid && password.touched">
                  <div class="invalid-feedback" *ngIf="password.invalid && password.touched">
                    <span *ngIf="password.errors?.['required']">Password is required</span>
                    <span *ngIf="password.errors?.['minlength']">Password must be at least 6 characters</span>
                  </div>
                </div>

                <div class="col-md-6 mb-3">
                  <label for="rollNumber" class="form-label">
                    <i class="fas fa-id-card me-1"></i>
                    Roll Number
                  </label>
                  <input 
                    type="text" 
                    class="form-control" 
                    id="rollNumber"
                    name="rollNumber"
                    [(ngModel)]="student.rollNumber"
                    placeholder="e.g., CS2021001">
                </div>
              </div>

              <div class="row" *ngIf="isEditMode">
                <div class="col-md-6 mb-3">
                  <label for="rollNumber" class="form-label">
                    <i class="fas fa-id-card me-1"></i>
                    Roll Number
                  </label>
                  <input 
                    type="text" 
                    class="form-control" 
                    id="rollNumber"
                    name="rollNumber"
                    [(ngModel)]="student.rollNumber"
                    placeholder="e.g., CS2021001">
                </div>

                <div class="col-md-6 mb-3">
                  <label for="phoneNumber" class="form-label">
                    <i class="fas fa-phone me-1"></i>
                    Phone Number
                  </label>
                  <input 
                    type="tel" 
                    class="form-control" 
                    id="phoneNumber"
                    name="phoneNumber"
                    [(ngModel)]="student.phoneNumber"
                    placeholder="e.g., +91 9876543210">
                </div>
              </div>

              <div class="row" *ngIf="!isEditMode">
                <div class="col-md-6 mb-3">
                  <label for="phoneNumber" class="form-label">
                    <i class="fas fa-phone me-1"></i>
                    Phone Number
                  </label>
                  <input 
                    type="tel" 
                    class="form-control" 
                    id="phoneNumber"
                    name="phoneNumber"
                    [(ngModel)]="student.phoneNumber"
                    placeholder="e.g., +91 9876543210">
                </div>

                <div class="col-md-6 mb-3">
                  <label for="course" class="form-label">
                    <i class="fas fa-graduation-cap me-1"></i>
                    Course
                  </label>
                  <select 
                    class="form-select" 
                    id="course"
                    name="course"
                    [(ngModel)]="student.course">
                    <option value="">Select Course</option>
                    <option value="Computer Science">Computer Science</option>
                    <option value="Information Technology">Information Technology</option>
                    <option value="Electronics">Electronics</option>
                    <option value="Mechanical">Mechanical</option>
                    <option value="Civil">Civil</option>
                  </select>
                </div>
              </div>

              <div class="row" *ngIf="isEditMode">
                <div class="col-md-6 mb-3">
                  <label for="course" class="form-label">
                    <i class="fas fa-graduation-cap me-1"></i>
                    Course
                  </label>
                  <select 
                    class="form-select" 
                    id="course"
                    name="course"
                    [(ngModel)]="student.course">
                    <option value="">Select Course</option>
                    <option value="Computer Science">Computer Science</option>
                    <option value="Information Technology">Information Technology</option>
                    <option value="Electronics">Electronics</option>
                    <option value="Mechanical">Mechanical</option>
                    <option value="Civil">Civil</option>
                  </select>
                </div>

                <div class="col-md-6 mb-3">
                  <label for="semester" class="form-label">
                    <i class="fas fa-calendar-alt me-1"></i>
                    Semester
                  </label>
                  <select 
                    class="form-select" 
                    id="semester"
                    name="semester"
                    [(ngModel)]="student.semester">
                    <option value="">Select Semester</option>
                    <option value="1">1st Semester</option>
                    <option value="2">2nd Semester</option>
                    <option value="3">3rd Semester</option>
                    <option value="4">4th Semester</option>
                    <option value="5">5th Semester</option>
                    <option value="6">6th Semester</option>
                    <option value="7">7th Semester</option>
                    <option value="8">8th Semester</option>
                  </select>
                </div>
              </div>

              <div class="row" *ngIf="!isEditMode">
                <div class="col-md-6 mb-3">
                  <label for="semester" class="form-label">
                    <i class="fas fa-calendar-alt me-1"></i>
                    Semester
                  </label>
                  <select 
                    class="form-select" 
                    id="semester"
                    name="semester"
                    [(ngModel)]="student.semester">
                    <option value="">Select Semester</option>
                    <option value="1">1st Semester</option>
                    <option value="2">2nd Semester</option>
                    <option value="3">3rd Semester</option>
                    <option value="4">4th Semester</option>
                    <option value="5">5th Semester</option>
                    <option value="6">6th Semester</option>
                    <option value="7">7th Semester</option>
                    <option value="8">8th Semester</option>
                  </select>
                </div>
              </div>

              <div class="d-flex justify-content-between mt-4">
                <button type="button" class="btn btn-secondary" (click)="goBack()">
                  <i class="fas fa-arrow-left me-2"></i>
                  Back to List
                </button>
                <button 
                  type="submit" 
                  class="btn btn-primary"
                  [disabled]="!studentForm.form.valid || isSubmitting">
                  <i class="fas fa-save me-2"></i>
                  <span *ngIf="!isSubmitting">{{ isEditMode ? 'Update Student' : 'Add Student' }}</span>
                  <span *ngIf="isSubmitting">
                    <i class="fas fa-spinner fa-spin me-2"></i>
                    {{ isEditMode ? 'Updating...' : 'Adding...' }}
                  </span>
                </button>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  `,
  styles: [`
    .card {
      border: none;
      box-shadow: 0 0 20px rgba(0, 0, 0, 0.1);
    }

    .form-label {
      font-weight: 600;
      color: #495057;
    }

    .form-control, .form-select {
      border: 2px solid #e9ecef;
      transition: all 0.3s ease;
    }

    .form-control:focus, .form-select:focus {
      border-color: #007bff;
      box-shadow: 0 0 0 0.2rem rgba(0, 123, 255, 0.25);
    }

    .btn {
      padding: 0.75rem 1.5rem;
      font-weight: 600;
      transition: all 0.3s ease;
    }

    .btn:hover {
      transform: translateY(-1px);
    }

    .invalid-feedback {
      font-size: 0.875rem;
    }
  `]
})
export class StudentFormComponent implements OnInit {
  student: Student = {
    name: '',
    email: '',
    password: '',
    rollNumber: '',
    phoneNumber: '',
    course: '',
    semester: undefined
  };

  isEditMode = false;
  isSubmitting = false;
  studentId: number | null = null;

  constructor(
    private studentService: StudentService,
    private router: Router,
    private route: ActivatedRoute
  ) { }

  ngOnInit(): void {
    this.route.params.subscribe(params => {
      if (params['id']) {
        this.isEditMode = true;
        this.studentId = +params['id'];
        this.loadStudent();
      }
    });
  }

  loadStudent(): void {
    if (this.studentId) {
      this.studentService.getStudentById(this.studentId).subscribe({
        next: (data) => {
          this.student = data;
          // Don't load password for security
          this.student.password = '';
        },
        error: (error) => {
          console.error('Error loading student:', error);
          this.router.navigate(['/students']);
        }
      });
    }
  }

  onSubmit(): void {
    if (this.isSubmitting) return;

    this.isSubmitting = true;

    if (this.isEditMode && this.studentId) {
      // Remove password from update payload
      const updateData = { ...this.student };
      delete updateData.password;

      this.studentService.updateStudent(this.studentId, updateData).subscribe({
        next: () => {
          this.router.navigate(['/students']);
        },
        error: (error) => {
          console.error('Error updating student:', error);
          this.isSubmitting = false;
        }
      });
    } else {
      this.studentService.createStudent(this.student).subscribe({
        next: () => {
          this.router.navigate(['/students']);
        },
        error: (error) => {
          console.error('Error creating student:', error);
          this.isSubmitting = false;
        }
      });
    }
  }

  goBack(): void {
    this.router.navigate(['/students']);
  }
}
